//SUP-4648
(function () {
  // Finds the sticky footer ad container
  const stickyFooter = document.querySelector('#pw-oop-bottom_rail');

  // Finds the cookie consent element (usually a banner)
  const cookieConsentAd = document.querySelector('div[aria-label^="cookieconsent"]');

  // Settings for watching changes
  const observerConfig = { attributes: true };

  // Function to update how much space is below the video
  const updateVideoOffset = () => {
    const iframe = window.GUMGUM?.isad?.iframe;  // iframe = the video container
    const videoPlayer = document.querySelector('.cnx-content-wrapper');

    if (iframe && videoPlayer) {
      const isScaled = iframe.style.transform === 'scale(1, 2)';
      const offset = isScaled ? 220 : 120; // Adjust spacing depending on size
      videoPlayer.style.marginBottom = `${offset}px`; // Add spacing below video
    }
  };

  // Watches for changes in the iframe's attributes
  const mutationCallback = (mutationsList) => {
    for (const mutation of mutationsList) {
      if (mutation.type === 'attributes') {
        updateVideoOffset();
      }
    }
  };

  const observer = new MutationObserver(mutationCallback);

  // Setup GumGum event handlers if not already set
  window.ggevents = window.ggevents || [];

  window.ggevents.push({
    // When the ad loads
    'inscreen.load': function () {
      if (stickyFooter) {
        stickyFooter.style.visibility = 'hidden'; // Hide sticky footer
      }

      if (cookieConsentAd) {
        cookieConsentAd.style.zIndex = '2147483647'; // Move on top
      }

      // Tell the page the ad has loaded
      document.body.dispatchEvent(new Event('gumgum-inscreen-load'));

      // Start watching the ad's iframe
      const iframe = window.GUMGUM?.isad?.iframe;
      if (iframe) {
        observer.observe(iframe, observerConfig);
      }
    },

    // When the ad is closed
    'inscreen.close': function (data) {
      if (stickyFooter) {
        stickyFooter.style.visibility = 'visible'; // Show sticky footer again
      }

      if (data?.userRequested) {
        document.body.dispatchEvent(new Event('gumgum-inscreen-close')); // Custom event
      }

      // Stop watching for changes
      observer.disconnect();

      if (cookieConsentAd) {
        cookieConsentAd.style.zIndex = '100'; // Put cookie banner back to normal
      }
    }
  });
})();
